import{_ as r}from"./plugin-vueexport-helper.2444895f.js";import{o as n,c as o,e as s,a as t,b as g,f as e}from"./app.28701975.js";const a={},i=t("p",null,[t("strong",null,"Python"),g(" \u9AD8\u6027\u80FD\u7F16\u7A0B\u7B80\u8FF0")],-1),h=e('<h2 id="_1-\u8BA1\u7B97\u673A\u57FA\u672C\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#_1-\u8BA1\u7B97\u673A\u57FA\u672C\u7CFB\u7EDF" aria-hidden="true">#</a> 1. \u8BA1\u7B97\u673A\u57FA\u672C\u7CFB\u7EDF</h2><h5 id="\u601D\u8003" tabindex="-1"><a class="header-anchor" href="#\u601D\u8003" aria-hidden="true">#</a> <strong>\u601D\u8003\uFF1A</strong></h5><p>\u5B9E\u73B0\u9AD8\u6027\u80FD <strong>Python</strong> \u4EE3\u7801\u7684\u969C\u788D\u5728\u54EA\u91CC\uFF1F</p><p>\u6027\u80FD\u95EE\u9898\u6709\u54EA\u4E9B\u79CD\u7C7B\uFF1F</p><p>\u4E00\u53F0\u8BA1\u7B97\u673A\u5E95\u5C42\u7EC4\u4EF6\u53EF\u5206\u4E3A\uFF1A <strong>\u8BA1\u7B97\u5355\u5143\u3001\u5B58\u50A8\u5355\u5143\u3001\u4E24\u8005\u95F4\u8FDE\u63A5</strong></p><h3 id="_1-1-\u8BA1\u7B97\u5355\u5143" tabindex="-1"><a class="header-anchor" href="#_1-1-\u8BA1\u7B97\u5355\u5143" aria-hidden="true">#</a> <strong>1.1 \u8BA1\u7B97\u5355\u5143</strong></h3><p><strong>\u8BA1\u7B97\u5355\u5143</strong>\uFF0C\u5373\u5C06\u63A5\u6536\u5230\u4EFB\u610F\u8F93\u5165\uFF08<em>\u6BD4\u7279</em> \uFF09\u8F6C\u4E3A\u8F93\u51FA\uFF08<em>\u8BA1\u7B97\u540E\u7684\u6BD4\u7279</em> \uFF09\u7684\u80FD\u529B\uFF0C\u4EE5\u53CA\u6539\u53D8\u5F53\u524D\u5904\u7406\u72B6\u6001\u7684\u80FD\u529B\uFF0C\u5373 <strong>CPU</strong> \u662F\u6700\u5E38\u89C1\u7684\u8BA1\u7B97\u5355\u5143\uFF0C\u5F53\u7136 \u4E4B\u524D\u8BBE\u8BA1\u4E0E\u56FE\u5F62\u52A0\u901F\u7684\u5904\u7406\u5355\u5143 <strong>GPU</strong> \u5982\u4ECA\u4E5F\u66F4\u9002\u5408\u7528\u4E8E\u6570\u503C\u8BA1\u7B97\u4E86</p><p><strong>\u4E3B\u8981\u5C5E\u6027\uFF1A</strong></p><ul><li><strong>IPC\uFF1A</strong> \u6BCF\uFF08<em>\u6307\u4EE4</em> \uFF09\u5468\u671F\u5B8C\u6210\u6307\u4EE4\u6570\uFF0C\u5F71\u54CD <strong>\u77E2\u91CF\u8BA1\u7B97</strong> \uFF08<em>\u4E00\u6B21\u63D0\u4F9B\u591A\u6570\u636E\u7ED9 <strong>\u4E00\u4E2A CPU</strong> \u540C\u65F6\u64CD\u4F5C</em> \uFF09</li><li><strong>\u65F6\u949F\u901F\u5EA6\uFF1A</strong> \u76EE\u524D\u7406\u89E3\u4E3A\u7A0B\u5E8F\u7684\u5207\u6362\u901F\u5EA6\uFF0C\u901F\u5EA6\u63D0\u9AD8\uFF0C\u4F1A\u7ACB\u5373 <strong>\u63D0\u9AD8\u8BA1\u7B97\u5355\u5143\u6240\u6709\u7A0B\u5E8F\u7684\u8FD0\u884C\u901F\u5EA6</strong></li></ul><p>\u8FD9\u4E24\u4E2A\u53C2\u6570\u662F\u4E92\u65A5\u7684\uFF0C\u4F46 <strong>GPU</strong> \u4E2D\uFF0C\u4E24\u53C2\u6570\u53EF\u4EE5\u90FD\u5F88\u9AD8\uFF08<em>\u4F46\u6709\u522B\u7684\u95EE\u9898</em> \uFF09</p><h5 id="\u8D85\u7EBF\u7A0B\u6280\u672F" tabindex="-1"><a class="header-anchor" href="#\u8D85\u7EBF\u7A0B\u6280\u672F" aria-hidden="true">#</a> <strong>\u8D85\u7EBF\u7A0B\u6280\u672F\uFF1A</strong></h5><p>\u7531\u4E8E\u6676\u4F53\u7BA1\u5DF2\u7ECF\u7F29\u5C0F\u81F3\u6781\u9650\uFF0C <strong>IPC</strong> \u548C \u65F6\u949F \u5DF2\u5230\u8FBE\u6781\u9650\uFF0C\u82AF\u7247\u5382\u5546\u7814\u5236\u4E86\u8D85\u7EBF\u7A0B\u6280\u672F\uFF0C\u5373 <strong>\u66F4\u806A\u660E\u7684\u4E71\u5E8F\u6267\u884C</strong> \u548C <strong>\u591A\u6838\u64CD\u4F5C</strong>\uFF0C\u5B83\u4E3A <strong>OS</strong> \u865A\u62DF\u4E86\u7B2C\u4E8C\u4E2A <strong>CPU</strong> \uFF0C\u5C06 <strong>\u4E24\u4E2A \u6307\u4EE4\u7EBF\u7A0B</strong> \u4EA4\u9519\u63D2\u5165 <strong>CPU</strong> \u7684 <strong>\u4E0D\u540C\u6267\u884C\u5355\u5143\uFF08<em>\u5982\uFF1A\u4E00\u4E2A\u64CD\u7EB5\u6574\u6570\u3001\u4E00\u4E2A\u64CD\u7EB5\u6D6E\u70B9\u6570</em>\uFF09</strong>\uFF0C\u82E5\u63D2\u5165\u6210\u529F\uFF0C\u5219\u8BA1\u7B97\u6027\u80FD\u6BD4\u5355\u7EBF\u7A0B\u63D0\u5347 <strong>30%</strong></p><ul><li><strong>\u4E71\u5E8F\uFF1A</strong> \u5141\u8BB8 <strong>\u7F16\u8BD1\u5668</strong> \u68C0\u6D4B\u51FA\u7EBF\u6027\u7A0B\u5E8F <strong>\u65E0\u4F9D\u8D56</strong> \u7684\u90E8\u5206\uFF0C\u5404\u79CD\u987A\u5E8F\u6267\u884C\u90FD\u4E0D\u4F1A\u5F71\u54CD\u6B63\u786E\u7ED3\u679C\uFF0C\u5373 <strong>\u8BA1\u7B97\u6B21\u5E8F\u4E0E\u7A0B\u5E8F\u8BBE\u8BA1\u4E0D\u540C\uFF0C\u4E5F\u80FD\u6B63\u786E\u8FD0\u884C</strong>\uFF0C\u8FD9\u5C31\u4F7F\u5F97\u4E00\u4E9B <strong>\u6307\u4EE4\u963B\u585E</strong> \u65F6\uFF08<em>\u5982\uFF1A\u7B49\u5F85\u4E00\u6B21\u5185\u5B58\u8BBF\u95EE</em> \uFF09\u53E6\u4E00\u4E9B\u6307\u4EE4\u4E5F\u5F97\u4EE5\u6267\u884C\uFF0C\u4EE5\u6B64\u6765\u63D0\u5347\u8D44\u6E90\u7684\u5229\u7528\u7387\u3002</li><li><strong>\u591A\u6838\u67B6\u6784\uFF1A</strong> \u5728\u540C\u4E00\u4E2A\u8BA1\u7B97\u5355\u5143\u4E2D\u5305\u542B\u4E86 <strong>\u591A\u4E2A CPU</strong> \uFF0C\u63D0\u9AD8\u4E86\u603B\u4F53\u8BA1\u7B97\u80FD\u529B\uFF0C\u4E14\u65E0\u9700\u7B49\u5F85\u5185\u5B58\u5C4F\u969C\uFF0C\u8BA9\u5355\u4E2A\u6838\u5FC3\u8DD1\u7684\u66F4\u5FEB</li></ul><div class="custom-container tip"><p class="custom-container-title">\u963F\u59C6\u8FBE\u5C14\u5B9A\u5F8B</p><p><strong>\u591A\u6838\u4E0D\u4E00\u5B9A\u610F\u5473\u7740\u901F\u5EA6\u7684\u63D0\u5347</strong> \uFF0C\u4E00\u4E2A\u53EF\u4EE5\u8FD0\u884C\u5728\u591A\u6838\u4E0A\u7684\u7A0B\u5E8F\uFF0C\u6709\u67D0\u4E9B <strong>\u8DEF\u5F84</strong> \u5FC5\u987B\u8FD0\u884C\u5728\u5355\u6838\u4E0A\uFF0C\u90A3\u4E48\u8FD9\u4E9B <strong>\u8DEF\u5F84</strong> \u5C31\u662F\u74F6\u9888\u5BFC\u81F4\u6700\u7EC8\u901F\u5EA6\u65E0\u6CD5\u901A\u8FC7 <strong>\u589E\u52A0\u66F4\u591A\u6838\u5FC3\u6765\u63D0\u9AD8</strong></p><p>\u56E0\u6B64\uFF0C\u4EFB\u4F55\u5E76\u884C\u8BA1\u7B97\u7684\u74F6\u9888\u6700\u7EC8\u90FD\u4F1A\u843D\u5728\u987A\u5E8F\u6267\u884C\u7684\u90A3\u90E8\u5206\u4EFB\u52A1\u4E0A</p></div><p>\u5BF9\u4E8E <strong>Python</strong> \u6765\u8BF4\uFF0C\u5145\u5206\u5229\u7528\u591A\u6838\u6027\u80FD\u7684\u963B\u788D\u4E3B\u8981\u5728\u4E8E <strong>GIL\uFF08<em>Python \u7684\u5168\u5C40\u89E3\u91CA\u5668\u9501</em> \uFF09</strong> \uFF0C\u5B83\u786E\u4FDD\u65E0\u8BBA\u6709\u591A\u5C11\u6838\u5FC3\uFF0C<strong>Python</strong> \u8FDB\u7A0B\u4E00\u6B21\u4E5F\u53EA\u80FD\u6267\u884C\u4E00\u6761\u6307\u4EE4\uFF0C\u5373\u4F7F\u4EE3\u7801\u53EF\u4F7F\u7528\u591A\u6838\u5FC3\uFF0C\u4F46\u5728\u4EFB\u610F\u4E00\u4E2A\u65F6\u95F4\u70B9\u4E0A\uFF0C\u4E5F\u4EC5\u6709\u4E00\u4E2A\u6838\u5FC3\u5728\u6267\u884C <strong>Python</strong> \u7684\u6307\u4EE4\uFF0C\u597D\u5728\u8FD9\u4E2A\u95EE\u9898\u53EF\u4EE5\u901A\u8FC7\u6807\u51C6\u5E93\u7684 <strong>multiprocessing</strong> \u6216 <strong>numexpr</strong> \u3001 <strong>Cpython</strong> \u7B49\u6280\u672F\uFF0C\u6216\u5206\u5E03\u5F0F\u8BA1\u7B97\u6A21\u578B\u6765\u89E3\u51B3</p><h3 id="_1-2-\u5B58\u50A8\u5355\u5143" tabindex="-1"><a class="header-anchor" href="#_1-2-\u5B58\u50A8\u5355\u5143" aria-hidden="true">#</a> <strong>1.2 \u5B58\u50A8\u5355\u5143</strong></h3><p>\u5B58\u50A8\u5355\u5143\uFF0C\u7528\u4E8E <strong>\u4FDD\u5B58\u6BD4\u7279</strong>\uFF0C\u8FD9\u4E9B\u6BD4\u7279\u53EF\u80FD\u662F\u7A0B\u5E8F\u4E2D\u7684\u53D8\u91CF\u3001\u4E00\u526F\u56FE\u7247\u7684\u50CF\u7D20\u7B49\u3002\u5176\u5305\u62EC\u4E86\u4E3B\u677F\u4E0A\u7684 <strong>\u5BC4\u5B58\u5668</strong> \u3001 <strong>RAM</strong> \u4EE5\u53CA <strong>\u786C\u76D8</strong> \uFF0C\u4E0D\u540C\u7C7B\u578B\u3001\u4ECB\u8D28\u3001\u534F\u8BAE\u7684\u5B58\u50A8\u5355\u5143\u7684\u533A\u522B\u5728\u4E8E\u5176 <strong>\u8BFB\u5199\u6570\u636E\u7684\u901F\u5EA6</strong>\uFF0C\u66F4\u590D\u6742\u7684\u95EE\u9898\u5728\u4E8E\u5176\u901F\u5EA6\u8FD8\u4E0E <strong>\u8BFB\u5199\u6570\u636E\u7684\u65B9\u5F0F\uFF08<em>\u987A\u5E8F\u3001\u968F\u673A</em> \uFF09</strong> \u7684\u65B9\u5F0F\u6709\u5173</p><blockquote><p>\u5982\u5927\u591A\u6570\u5B58\u50A8\u5355\u5143 \u4E00\u6B21\u8BFB\u4E00\u5927\u5757\u6570\u636E\u7684\u6027\u80FD\u8FDC\u597D\u4E8E\u8BFB\u591A\u6B21\u5C0F\u5757\u6570\u636E\uFF0C\u5C06\u5176\u60F3\u8C61\u6210\u4E00\u672C\u4E66\u4E2D\u7684\u4E66\u9875\uFF0C\u5927\u591A\u6570\u5B58\u50A8\u5355\u5143\u7684\u8BFB\u5199\u901F\u5EA6\u5728\u8FDE\u7EED\u7FFB\u9875\u65F6\u9AD8\u4E8E\u4ECE\u4E00\u5F20\u968F\u673A\u9875\u8DF3\u81F3\u53E6\u4E00\u5F20\u968F\u5373\u9875</p></blockquote><h3 id="_1-3-\u901A\u4FE1\u5C42" tabindex="-1"><a class="header-anchor" href="#_1-3-\u901A\u4FE1\u5C42" aria-hidden="true">#</a> <strong>1.3 \u901A\u4FE1\u5C42</strong></h3><p>\u4E0A\u8FF0\u57FA\u672C\u5355\u5143\u901A\u4FE1\uFF0C\u9700\u8981\u4F9D\u8D56 <strong>\u603B\u7EBF</strong></p>',20);function d(c,p){return n(),o("div",null,[i,s(" more "),h])}const _=r(a,[["render",d],["__file","\u9AD8\u6027\u80FD\u7F16\u7A0B.html.vue"]]);export{_ as default};
